import React, {useState} from 'react';
import "./TextArea.css";

export default function TextArea() {
    const [text, updateText] = useState("Enter the text you want to capitalize")
    let onChangeHandle=(e)=>{
        updateText(e.target.value)
    }
    let onClickHandle = ()=>{
        updateText.to
    }
    return (
        <>
            <div className="container">
                <strong>The text you want to change into uppercase</strong>
                <label htmlFor="text-area"></label>
                <textarea id="text-area" placeholder="Enter your Text" value={text} onChange={onChangeHandle}></textarea>
                <button onClick={onClickHandle}>Click to change to UpperCase</button>
            </div>
        </>
    )
}
