import './App.css';
import Navbar from './Components/Navbar';
import TextArea from './Components/TextArea';
import React, { useState } from 'react'
import Contact from './Components/Contact';
import { Route, Routes } from 'react-router-dom';


function App() {
  const [modeEnabled, updateModeEnabled] = useState("#1F2633")
  const [textModeEnabled, updateTextModeEnabled] = useState("white")
  const [modeTextNav, updateModeTextNav] = useState("Dark")
  const [translateXValue, updateTranslateXValue] = useState("0px")
  const [labelBackgroundColor, updateLabelBackgroundColor] = useState("#c5d1dc84")
  const [btnBgColor, updateBtnBgColor] = useState("green")
  const [textColor, updateTextColor] = useState("black")



  const enableDarkModeOnClick = () => {
    // modeEnabled==="black"?updateModeEnabled("white"):updateModeEnabled("black")

    switch (modeEnabled) {
      case "#1F2633":
        document.body.style.backgroundColor = "#1F2633"
        updateModeEnabled("rgb(185, 192, 198)")
        updateTextModeEnabled("black")
        updateModeTextNav("Light")
        updateTranslateXValue("+70%")
        updateLabelBackgroundColor("#00D1FF")
        updateBtnBgColor("rgb(185, 192, 198)")
        updateTextColor("white")
        break;
      default:
        document.body.style.backgroundColor = "white"
        updateModeEnabled("#1F2633")
        updateTextModeEnabled("white")
        updateModeTextNav("Dark")
        updateTranslateXValue("0px")
        updateLabelBackgroundColor("#c5d1dc84")
        updateBtnBgColor("green")
        updateTextColor("black")
        break;
    }


  }
  return (
    <>
      <Routes>
      <Navbar enableDarkModeOnClick={enableDarkModeOnClick} darkLightModeNav={{ backgroundColor: modeEnabled }} darkLightModeTextNav={modeTextNav} darkLightModeText={{ color: textModeEnabled }} transitionCircle={{ transform: `translateX( ${translateXValue} )` }} backgroundColorDarkMode={{ backgroundColor: labelBackgroundColor }} />
        <Route path="/">
          <TextArea backgroundColorButton={{ backgroundColor: btnBgColor, color: textModeEnabled }} textAreaH3Color={{ color: textColor }} />
        </Route>
        <Route path="/contact">
          <Contact />
        </Route>
      </Routes>
    </>
  );
}

export default App;
