import './App.css';
import Navbar from './Components/Navbar';
import TextArea from './Components/TextArea';
import React, { useState } from 'react'


function App() {
  const [modeEnabled, updateModeEnabled] = useState("black")
  const [textModeEnabled, updateTextModeEnabled] = useState("white")
  const [modeTextNav, updateModeTextNav] = useState("Dark")
  const [translateXValue, updateTranslateXValue] = useState("0px")


  const enableDarkModeOnClick=()=>{
    modeEnabled==="black"?updateModeEnabled("white"):updateModeEnabled("black")
    modeEnabled==="black"?updateTextModeEnabled("black"):updateTextModeEnabled("white")
    modeEnabled==="black"?updateModeTextNav("Light"):updateModeTextNav("Dark")
    modeEnabled==="black"?updateTranslateXValue("+70%"):updateTranslateXValue("0px")

    switch (modeEnabled) {
      case "black":
        updateModeEnabled("white")
        updateTextModeEnabled("black")
        updateModeTextNav("Light")
        updateTranslateXValue("+70%")
        break;

      default:
        break;
    }


  }
  return (
    <>
    <Navbar enableDarkModeOnClick={enableDarkModeOnClick} darkLightModeNav={{backgroundColor:modeEnabled}} darkLightModeTextNav={modeTextNav} darkLightModeText={{color:textModeEnabled}} transitionCircle={{transform : `translateX( ${translateXValue} )`}}/>
    <TextArea/>
    </>
  );
}

export default App;
