import './App.css';
import Navbar from './Components/Navbar';
import TextArea from './Components/TextArea';
import React, { useState } from 'react'


function App(props) {
  const [modeEnabled, updateModeEnabled] = useState("black")
  const [textModeEnabled, updateTextModeEnabled] = useState("white")
  const [modeTextNav, updateModeTextNav] = useState("white")

 // let enableDarkModeOnClick=()=>{
  // }
  const enableDarkModeOnClick=()=>{
    // console.log(darkLightModeNav.style)
    updateModeEnabled("white")
    updateTextModeEnabled("black")
    updateModeTextNav("black")

  }
  return (
    <>
    <Navbar enableDarkModeOnClick={enableDarkModeOnClick} darkLightModeNav={{backgroundColor:modeEnabled}} darkLightModeTextNav={modeTextNav} darkLightModeText={{color:textModeEnabled}}/>
    <TextArea/>
    </>
  );
}

export default App;
