export default function TextArea() {
    const [text, updateText] = useState("Enter the text you want to capitalize")
  return (
    <>
    <div className="container">
      <label htmlFor="text-area">The text you want to change into uppercase</label>
      <textarea id="text-area" placeholder="Enter your Text">{text}</textarea>
    </div>
    </>
  )
}
