import './App.css';
import Navbar from './Components/Navbar';
import TextArea from './Components/TextArea';
import React, { useState } from 'react'
import Contact from './Components/Contact';
import { Route, Routes } from 'react-router-dom';
import { useEffect } from "react";
import { useLocation } from "react-router-dom";
// import (use)


function App() {
    // const [modeEnabled, updateModeEnabled] = useState("#1F2633")




    const [state, setState] = useState({
      modeEnabled: "#1F2633",
      textModeEnabled: 'white',
      modeTextNav: 'Dark',
      translateXValue: '0px',
      labelBackgroundColor: '#c5d1dc84',
      btnBgColor: 'green',
      textColor: "black",
      contactPgUnderline: "none",
    })




  // useHistory().push("/")

  // let clicking=()=>{
    // if (window.location.pathname === "/contact") {
    //   setState({ contactPgUnderline: "underline" })
    //   console.log("contact")
    // };
  // }

  const enableDarkModeOnClick = () => {
    console.log(state.modeEnabled)
    // console.log(state.modeTextNav)
    // console.log(state)
    if (state.modeEnabled === "#1F2633") {
      console.log("dark mode click")
      document.body.style.backgroundColor = "#1F2633"
      setState({...state, modeEnabled: "rgb(185, 192, 198)",...state,  textModeEnabled: "black",...state,  modeTextNav: "Light",...state,  translateXValue: "+70%",...state,  btnBgColor: "rgb(185, 192, 198)",...state,  textColor: "white",...state,  labelBackgroundColor: "#00D1FF" })
    }
    else {
      document.body.style.backgroundColor = "white"
      setState({ ...state, modeEnabled: "#1F2633", ...state, textModeEnabled: "white", ...state, modeTextNav: "Dark", ...state, translateXValue: "0px", ...state, btnBgColor: "green", ...state, textColor: "black", ...state, labelBackgroundColor: "#c5d1dc84" })
    }

  }


  return (
    <>
      <Navbar enableDarkModeOnClick={enableDarkModeOnClick} darkLightModeNav={{ backgroundColor: state.modeEnabled }} darkLightModeTextNav={state.modeTextNav} darkLightModeText={{ color: state.textModeEnabled }} transitionCircle={{ transform: `translateX( ${state.translateXValue} )` }} backgroundColorDarkMode={{ backgroundColor: state.labelBackgroundColor }} currentPgIsContact={{textDecoration : state.contactPgUnderline}} />
      <Routes>
        <Route path="/" element={<TextArea backgroundColorButton={{ backgroundColor: state.btnBgColor, color: state.textModeEnabled }} textAreaColor={{ color: state.textColor }} />}>
        </Route>
        <Route path="/contact" element={<Contact textAreaColor={{ color: state.textColor }} />}>
        </Route>
      </Routes>
    </>
  );

}


export default App;
