import React, {useState} from 'react';
import "./TextArea.css";

export default function TextArea() {
    const [text, updateText] = useState("")
    let [numberOfWords, updateNumberOfWords] = useState(0)
    let onChangeHandle=(e)=>{
        updateText(e.target.value)

    }
    let onInputHandle =()=>{
        updateNumberOfWords(text.length)
        console.log(text)
    }
    let clickToUpperCase = ()=>{
        updateText(text.toUpperCase());
    }
    let clickToLowerCase = ()=>{
        updateText(text.toLowerCase());
    }
   
    return (
        <>
            <div className="container">
                <strong>The text you want to change into uppercase</strong>
                <label htmlFor="text-area"></label>
                <textarea id="text-area" placeholder="Enter your Text" value={text} onChange={onChangeHandle} onC={onInputHandle}></textarea>
                <div className="buttons">
                <button onClick={clickToUpperCase}>Click to change to UpperCase</button>
                <button onClick={clickToLowerCase}>Click to change to LowerCase</button>
                </div>
                <div className="numberOfWords">
                    <h1>The Number of Words are <span>{numberOfWords}</span></h1>
                </div>
            </div>
        </>
    )
}
