import { useState } from "react"
import "./Navbar.css"

export default function Navbar(props) {
    const [darkLightModeText, updateDarkLightModeText] = useState("Dark")
    const [modeEnabled, updateModeEnabled] = useState("black")
    const [textModeEnabled, updateTextModeEnabled] = useState("white")

    const enableDarkModeOnClick=()=>{

        
        updateModeEnabled("white")
        updateTextModeEnabled("black")
    
      }
    return (
        <>
            <nav style={{backgroundColor:modeEnabled}}>
                <section className="rightSideNav">
                    <h1 style={{color:textModeEnabled}}>Text Tool Services</h1>
                    <div className="about" style={{color:textModeEnabled}}>About</div>
                    <div className="contacts" style={{color:textModeEnabled}}>Contact Us</div>
                </section>
                <section className="leftSideNav">
                        <input type="checkbox" className="dark-light-mode-checkbox" aria-label="Choose to enable dark or light mode"/>
                        <label htmlFor="dark-light-mode-label" className="dark-light-mode-toggle-label" onClick={enableDarkModeOnClick}>
                            <div className="dark-light-mode-enabled-circle"></div>
                        </label>
                        <h4 style={{color:textModeEnabled}}>Enable {darkLightModeText} Mode</h4>
                </section>
            </nav>
        </>
    )
}
