import './App.css';
import Navbar from './Components/Navbar';
import TextArea from './Components/TextArea';
import React, { useState } from 'react'


function App() {
  const [modeEnabled, updateModeEnabled] = useState("black")
  const [textModeEnabled, updateTextModeEnabled] = useState("white")
  const [modeTextNav, updateModeTextNav] = useState("Dark")
  const [translateXValue, updateTranslateXValue] = useState("0")


  const enableDarkModeOnClick=()=>{
    modeEnabled==="black"?updateModeEnabled("white"):updateModeEnabled("black")
    modeEnabled==="black"?updateTextModeEnabled("black"):updateTextModeEnabled("white")
    modeEnabled==="black"?updateModeTextNav("Light"):updateModeTextNav("Dark")
    updateTranslateXValue("+50px")

  }
  return (
    <>
    <Navbar enableDarkModeOnClick={enableDarkModeOnClick} darkLightModeNav={{backgroundColor:modeEnabled}} darkLightModeTextNav={modeTextNav} darkLightModeText={{color:textModeEnabled}} transitionCircle={{transition : `translateX(translateXValue)`}}/>
    <TextArea/>
    </>
  );
}

export default App;
