//   const [textColor, updateTextColor] = useState("black")
  // const [detectCurrentPageUnderline, updateDetectCurrentPageUnderline] = useState("none")
  const [state, setState] = useState({
    modeEnabled: "#1F2633",
    textModeEnabled: 'white',
    modeTextNav: 'Dark',
    translateXValue: '0px',
    labelBackgroundColor: '#c5d1dc84',
    btnBgColor: 'green'
  })
  // detectCurrentPageUnderline


  // const location = useLocation();

  // useEffect(() => {
  //   findingCurrentRoutePath();
  //   console.log(window.location.pathname)
  // },[window.location.pathname]);

  // function findingCurrentRoutePath(){
    // console.log(location.pathname[0])
    // window.onload=()=>{
    //   switch (window.location.pathname) {
    //     case "/contact":
    //       break;
    //       case "/":
    //         console.log("home")
    //         // updateDetectCurrentPageUnderline("underline")
    //         break;
    //       default:
    //         // updateDetectCurrentPageUnderline("none")

    //       }
    //     }
  // }

  // findingCurrentRoutePath();
  // const detectCurrentPages = "underline"
  // }

  //Your initialization



  const enableDarkModeOnClick = () => {
    setState({modeEnabled : "blue"})
    // modeEnabled==="black"?updateModeEnabled("white"):updateModeEnabled("black")

    switch (state.modeEnabled) {
      case "#1F2633":
        document.body.style.backgroundColor = "#1F2633"
        setState({modeEnabled : "rgb(185, 192, 198)"})
        setState({textModeEnabled : "black"})
        setState({modeTextNav : "Light"})
        setState({translateXValue : "+70%"})
        setState({labelBackgroundColor : "#00D1FF"})
        setState({btnBgColor : "rgb(185, 192, 198)"})
        setState({textColor : "white"})
        break;
      default:
        document.body.style.backgroundColor = "white"
        setState({modeEnabled : "#1F2633"})
        setState({textModeEnabled : "white"})
        setState({modeTextNav : "Dark"})
        setState({translateXValue : "0px"})
        setState({labelBackgroundColor : "#c5d1dc84"})
        setState({btnBgColor : "rgb(185, 192, 198)"})
        setState({textColor : "white"})
        // updateModeEnabled("#1F2633")
        // updateTextModeEnabled("white")
        // updateModeTextNav("Dark")
        // updateTranslateXValue("0px")
        // updateLabelBackgroundColor("#c5d1dc84")
        // updateBtnBgColor("green")
        // updateTextColor("black")
        break;
    }

  }
  return (
    <>
      <Navbar enableDarkModeOnClick={enableDarkModeOnClick} darkLightModeNav={{ backgroundColor: state.modeEnabled}}
      darkLightModeTextNav={state.modeTextNav} darkLightModeText={{ color: state.textModeEnabled}} detectCurrentPage={state.detectCurrentPages} transitionCircle={{ transform: `translateX( ${state.translateXValue} )` }} backgroundColorDarkMode={{ backgroundColor: state.labelBackgroundColor }} />
      <Routes>
        <Route path="/" element={<TextArea backgroundColorButton={{ backgroundColor: state.btnBgColor, color: state.textModeEnabled }} textAreaColor={{ color: state.textColor }} />}>
        </Route>
        <Route path="/contact" element={<Contact textAreaColor={{ color: state.textColor }} />}>
        </Route>
      </Routes>
    </>
  );
}

export default App;
